import { useContext, useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import UserContext from '../UserContext';

export default function Profile() {
  const { user, setUser } = useContext(UserContext);
  const [firstName, setFirstName] = useState('');
  const [middleName, setMiddleName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  // Autofill data when component mounts
  useEffect(() => {
    if (user && user._id) {
      setFirstName(user.firstName || '');
      setMiddleName(user.middleName || '');
      setLastName(user.lastName || '');
      setEmail(user.email || '');
      setContactNumber(user.contactNumber || '');
    } else {
      // If user is not found, redirect to login page
      navigate('/login');
    }
  }, [user, navigate]);

  const handleUpdate = (e) => {
    e.preventDefault();

    // Validate the form fields
    if (!firstName || !lastName || !email) {
      setErrorMessage('Please fill in all required fields!');
      return;
    }

    const updatedUser = {
      firstName,
      middleName,
      lastName,
      email,
      contactNumber,
      password,
    };

    // API call to update user profile
    fetch("http://localhost:4000/user/edit", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify(updatedUser),
    })
      .then((result) => result.json())
      .then((data) => {
        if (data.code === 'USER-UPDATED') {
          // Successfully updated the user, update context and navigate
          setUser({
            ...user,
            firstName,
            middleName,
            lastName,
            email,
            contactNumber,
          });
          alert('Profile updated successfully!');
          navigate('/'); // Redirect to homepage after update
        } else {
          setErrorMessage(data.message || 'Error updating profile!');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        setErrorMessage('Failed to update profile!');
      });
  };

  return (
    <Container fluid className="vh-100 d-flex flex-column justify-content-center align-items-center">
      <Row className="w-50">
        <Col>
          <Form onSubmit={handleUpdate}>
            <h1 className="display-5 fw-bold mb-4">Update Profile</h1>

            {errorMessage && (
              <div className="alert alert-danger" role="alert">
                {errorMessage}
              </div>
            )}

            {/* First Name */}
            <Form.Group className="mb-3" controlId="formFirstName">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your first name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                required
              />
            </Form.Group>

            {/* Middle Name */}
            <Form.Group className="mb-3" controlId="formMiddleName">
              <Form.Label>Middle Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your middle name"
                value={middleName}
                onChange={(e) => setMiddleName(e.target.value)}
              />
            </Form.Group>

            {/* Last Name */}
            <Form.Group className="mb-3" controlId="formLastName">
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your last name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                required
              />
            </Form.Group>

            {/* Email */}
            <Form.Group className="mb-3" controlId="formEmail">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            {/* Contact Number */}
            <Form.Group className="mb-3" controlId="formContactNumber">
              <Form.Label>Contact Number</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your contact number"
                value={contactNumber}
                onChange={(e) => setContactNumber(e.target.value)}
              />
            </Form.Group>

            {/* Password */}
            <Form.Group className="mb-3" controlId="formPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter new password (if changing)"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Button variant="primary" type="submit" className="w-100">
              Update
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}
