import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../UserContext'; // Import UserContext to get user data
import { Button, Form, Container } from 'react-bootstrap';

function Profile() {
  const { user, setUser } = useContext(UserContext); // Get user and setUser from context
  const [firstName, setFirstName] = useState('');
  const [middleName, setMiddleName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    if (user.id) {
      // Fetch user details if user is logged in
      fetch(`http://localhost:4000/users/details/${user.id}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      })
        .then(result => result.json())
        .then(data => {
          if (data.result) {
            setFirstName(data.result.firstName);
            setMiddleName(data.result.middleName);
            setLastName(data.result.lastName);
            setEmail(data.result.email);
            setContactNumber(data.result.contactNumber);
            setPassword(''); // Keep password empty
          }
        });
    }
  }, [user.id]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedUser = {
      firstName,
      middleName,
      lastName,
      email,
      contactNumber,
      password,
    };

    // Send updated user data to backend for updating profile
    fetch(`http://localhost:4000/users/update/${user.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify(updatedUser),
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          setUser({
            ...user,
            firstName,
            middleName,
            lastName,
            email,
            contactNumber,
          });
          alert('Profile updated successfully');
        } else {
          alert('Failed to update profile');
        }
      })
      .catch(err => console.error(err));
  };

  return (
    <Container className="my-5">
      <h2>Profile Information</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="firstName">
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter first name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="middleName" className="mt-3">
          <Form.Label>Middle Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter middle name"
            value={middleName}
            onChange={(e) => setMiddleName(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="lastName" className="mt-3">
          <Form.Label>Last Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter last name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="email" className="mt-3">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="contactNumber" className="mt-3">
          <Form.Label>Contact Number</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter contact number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId="password" className="mt-3">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter new password (if changing)"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" type="submit" className="mt-4">
          Update Profile
        </Button>
      </Form>
    </Container>
  );
}

export default Profile;
