import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({
    firstName: "",
    middleName: "",
    lastName: "",
    email: "",
    contactNumber: "",
  });
  const [firstName, setFirstName] = useState("");
  const [middleName, setMiddleName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  // Fetch user data on load
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      navigate("/login");  // Redirect to login if no token found
      return;
    }

    // Fetch user profile data
    fetch("http://localhost:4000/user/profile", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.code === "USER-FOUND") {
          setUser(data.user);
          setFirstName(data.user.firstName);
          setMiddleName(data.user.middleName);
          setLastName(data.user.lastName);
          setEmail(data.user.email);
          setContactNumber(data.user.contactNumber);
        } else {
          setErrorMessage(data.message || "Error fetching user data");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        setErrorMessage("Failed to fetch profile data!");
      });
  }, [navigate]);

  // Handle profile update
  const handleUpdateProfile = () => {
    const updatedUser = {
      firstName,
      middleName,
      lastName,
      email,
      contactNumber,
      password,
    };

    // Log data to verify
    console.log(updatedUser);

    const token = localStorage.getItem("token");

    if (!token) {
      setErrorMessage("Token not found. Please log in again.");
      navigate("/login");
      return;
    }

    // API call to update user profile
    fetch("http://localhost:4000/user/edit", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(updatedUser),
    })
      .then((response) => {
        console.log(response);  // Log response
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.code === "USER-UPDATED") {
          setUser({
            ...user,
            firstName,
            middleName,
            lastName,
            email,
            contactNumber,
          });
          alert("Profile updated successfully!");
          navigate("/"); // Redirect to home after success
        } else {
          setErrorMessage(data.message || "Error updating profile!");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        setErrorMessage(`Failed to update profile: ${error.message}`);
      });
  };

  return (
    <div className="profile-container">
      <h2>Edit Profile</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <form>
        <label>First Name</label>
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <label>Middle Name</label>
        <input
          type="text"
          value={middleName}
          onChange={(e) => setMiddleName(e.target.value)}
        />
        <label>Last Name</label>
        <input
          type="text"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <label>Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label>Contact Number</label>
        <input
          type="text"
          value={contactNumber}
          onChange={(e) => setContactNumber(e.target.value)}
        />
        <label>Password</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="button" onClick={handleUpdateProfile}>
          Update Profile
        </button>
      </form>
    </div>
  );
};

export default Profile;
