import { useState, useEffect, useContext } from "react";
import { Container, Row, Col, Button, Form } from "react-bootstrap";
import Swal from "sweetalert2";
import UserContext from "../UserContext";
import { Navigate } from "react-router-dom";

export default function Profile() {
    const { user, setUser } = useContext(UserContext);

    const [firstName, setFirstName] = useState("");
    const [middleName, setMiddleName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [contactNumber, setContactNumber] = useState("");
    const [password, setPassword] = useState("");

    useEffect(() => {
        if (user.id) {
            fetchUserProfile();
        }
    }, [user.id]);

    // Fetch user details
    const fetchUserProfile = () => {
        const token = localStorage.getItem("token");
        fetch("http://localhost:4000/users/details", {
            method: "POST",
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
        .then((result) => result.json())
        .then((data) => {
            if (data.result) {
                setFirstName(data.result.firstName);
                setMiddleName(data.result.middleName);
                setLastName(data.result.lastName);
                setEmail(data.result.email);
                setContactNumber(data.result.contactNumber);
            }
        })
        .catch((error) => {
            Swal.fire({
                title: "Error fetching user profile",
                text: error.message,
                icon: "error",
            });
        });
    };

    // Update user profile
    const updateProfile = (e) => {
        e.preventDefault();

        const token = localStorage.getItem("token");
        const updatedData = {
            firstName,
            middleName,
            lastName,
            email,
            contactNumber,
            password,
        };

        fetch("http://localhost:4000/users/update", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify(updatedData),
        })
        .then((result) => result.json())
        .then((result) => {
            if (result.code === "USER-UPDATED") {
                Swal.fire({
                    title: "Profile Updated!",
                    text: result.message,
                    icon: "success",
                });
                setUser({
                    ...user,
                    firstName: result.user.firstName,
                    middleName: result.user.middleName,
                    lastName: result.user.lastName,
                    email: result.user.email,
                    contactNumber: result.user.contactNumber,
                });
            } else {
                Swal.fire({
                    title: "Failed to update profile",
                    text: result.message,
                    icon: "error",
                });
            }
        })
        .catch((error) => {
            Swal.fire({
                title: "Error updating profile",
                text: error.message,
                icon: "error",
            });
        });
    };

    return (
        user.id !== null ? (
            <Container fluid className="vh-100">
                <Row>
                    <Col className="vh-100 bg-warning col-6 d-flex flex-column align-items-center justify-content-center text-center">
                        <h1 className="display-5 fw-bold">Your Profile</h1>
                        <p className="display-6">Manage your personal details</p>
                    </Col>

                    <Col className="vh-100 col-6">
                        <Container fluid className="p-5 d-flex flex-column align-items-center justify-content-center vh-100">
                            <Form
                                className="w-100 p-5 shadow rounded-3 border-bottom border-3 border-warning"
                                onSubmit={updateProfile}
                            >
                                <h1 className="display-5 fw-bold mb-5">Edit Profile</h1>

                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        placeholder="First Name"
                                        value={firstName}
                                        onChange={(e) => setFirstName(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        placeholder="Middle Name"
                                        value={middleName}
                                        onChange={(e) => setMiddleName(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        placeholder="Last Name"
                                        value={lastName}
                                        onChange={(e) => setLastName(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="email"
                                        placeholder="Email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        placeholder="Contact Number"
                                        value={contactNumber}
                                        onChange={(e) => setContactNumber(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="password"
                                        placeholder="New Password (Optional)"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Button
                                        variant="warning"
                                        className="w-100 rounded-pill"
                                        type="submit"
                                    >
                                        Update Profile
                                    </Button>
                                </Form.Group>
                            </Form>
                        </Container>
                    </Col>
                </Row>
            </Container>
        ) : (
            <Navigate to="/login" />
        )
    );
}
