import { useContext, useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import UserContext from '../UserContext';

export default function Profile() {
  const { user, setUser } = useContext(UserContext);
  const [name, setName] = useState(user.name || '');
  const [email, setEmail] = useState(user.email || '');
  const navigate = useNavigate();

  const handleUpdate = (e) => {
    e.preventDefault();

    fetch("http://localhost:4000/users/update", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`
      },
      body: JSON.stringify({ name, email })
    })
    .then(result => result.json())
    .then(data => {
      if (data.success) {
        setUser({
          ...user,
          name,
          email
        });
        alert('Profile updated successfully!');
        navigate('/'); // Redirect to homepage after update
      } else {
        alert('Error updating profile!');
      }
    });
  };

  return (
    <Container fluid className="vh-100 d-flex flex-column justify-content-center align-items-center">
      <Row className="w-50">
        <Col>
          <Form onSubmit={handleUpdate}>
            <h1 className="display-5 fw-bold mb-4">Update Profile</h1>
            <Form.Group className="mb-3" controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formEmail">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit" className="w-100">
              Update
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}
